#import "HJGHomeView.h"
#import "HJGHomeCollectionViewCell.h"
#import "HJGHomeDetailController.h"
@interface HJGHomeView()<UICollectionViewDelegate, UICollectionViewDataSource, UICollectionViewDelegateFlowLayout>

//collection
@property (nonatomic, strong) UICollectionView *collection;

@property (nonatomic, strong) NSArray *image_name_arr;

@property (nonatomic, strong) NSArray *dis_name_arr;

@property (nonatomic, strong) NSArray *gushi_arr;

@end
@implementation HJGHomeView


- (instancetype)initWithFrame:(CGRect)frame{
    if (self = [super initWithFrame:frame]) {
        [self setupUI];
    }
    return self;
}


- (void)setupUI{
    

    [self loadGushi];
    
    self.image_name_arr = @[@"11",@"22",@"33.jpg",@"44.jpg",@"55",@"66",@"77.jpg",@"88",@"99",@"1010",@"1111",@"1212",@"1313",@"1414",@"1515",@"1616"];
    
    self.dis_name_arr = @[@"毛驴王子的故事",@"穷人和富人的故事",@"红房子和绿房子",@"花儿选美",@"三个幸运儿",@"画眉嘴国王",@"小木偶奇遇记",@"真假米老鼠",@"蜗牛救火队",@"骄傲的小乌龟",@"有魔力的狐狸",@"当掉国王的剑",@"狼和七只小羊",@"勇敢的小裁缝",@"阳光的故事",@"妖山"];
    
    [self backImageView];
    
    [self setTheCollect];
    
}


- (void)setTheCollect{
    
    //collection
    UICollectionViewFlowLayout *layout = [[UICollectionViewFlowLayout alloc]init];
    layout.minimumLineSpacing = 0;
    layout.minimumInteritemSpacing = 0;
    layout.sectionInset = UIEdgeInsetsMake(H(10),W(10),H(10),W(10));
    self.collection = [[UICollectionView alloc]initWithFrame:CGRectMake(W(40), kMarginTopHeight + H(40), WIDTH- W(80), HEIGHT - kMarginTopHeight - H(85) - kTabBarHeight) collectionViewLayout:layout];
    self.collection.delegate = self;
    self.collection.dataSource = self;
    self.collection.backgroundColor = [UIColor clearColor];
    self.collection.showsVerticalScrollIndicator = NO;

    [self.collection registerClass:[HJGHomeCollectionViewCell class] forCellWithReuseIdentifier:HJGHomeC];

    [self addSubview:self.collection];
    
}


#pragma mark - collectionDelegate and dataSourse


- (NSInteger)numberOfSectionsInCollectionView:(UICollectionView *)collectionView{
    return 1;
}

- (NSInteger)collectionView:(UICollectionView *)collectionView numberOfItemsInSection:(NSInteger)section{
    return 16;
}

- (CGSize)collectionView:(UICollectionView *)collectionView layout:(UICollectionViewLayout *)collectionViewLayout sizeForItemAtIndexPath:(NSIndexPath *)indexPath{
    return CGSizeMake((WIDTH- W(10))/3.0, H(140));
}

- (UICollectionViewCell *)collectionView:(UICollectionView *)collectionView cellForItemAtIndexPath:(NSIndexPath *)indexPath{
    HJGHomeCollectionViewCell *cell = (HJGHomeCollectionViewCell *)[collectionView dequeueReusableCellWithReuseIdentifier:HJGHomeC forIndexPath:indexPath];
    cell.backgroundColor = [UIColor clearColor];
    cell.TopIcon.image = [UIImage imageNamed:[self.image_name_arr safeObjectAtIndex:indexPath.row]];
    cell.bottomLab.text = [self.dis_name_arr safeObjectAtIndex:indexPath.row];
    return cell;
}

- (UIEdgeInsets)collectionView:(UICollectionView *)collectionView layout:(UICollectionViewLayout *)collectionViewLayout insetForSectionAtIndex:(NSInteger)section{
    return UIEdgeInsetsMake(H(10), W(10), H(10), W(10));
}

- (void)collectionView:(UICollectionView *)collectionView didSelectItemAtIndexPath:(NSIndexPath *)indexPath{
    
    
    HJGHomeDetailController *vc = [[HJGHomeDetailController alloc]init];
    vc.gushi = [self.gushi_arr safeObjectAtIndex:indexPath.row];
    vc.title_str = [self.dis_name_arr safeObjectAtIndex:indexPath.row];
    [self.vc.navigationController pushViewController:vc animated:YES];
    
}


- (UIImageView *)backImageView
{
    if (!_backImageView) {
        UIImageView * theView = [[UIImageView alloc] init];
        theView.userInteractionEnabled = YES;
        theView.image = [UIImage imageNamed:@"backHome.jpg"];
        [self addSubview:theView];
        [theView makeConstraints:^(MASConstraintMaker *make) {
            make.left.bottom.right.top.equalTo(self);
        }];
        _backImageView = theView;
    }
    return _backImageView;
}

- (void)loadGushi{

    NSString *one = @"    从前，有个国王和王后，他们很富有，简直享有一切他们所希望的东西，只是没有孩子。王后为此日夜伤感，说：“我就像块不长庄稼的地。”上帝最后成全了他，给他了个孩子，但这孩子根本不像人，而是头小毛驴。母亲不见则已见了叫苦连天，她说自己宁可不要孩子也不愿有头驴，并且还想把他扔进河里让鱼吃掉。但国王却说：“别这样，既然上帝把他赐给我们，他就该是我的儿子和继续人，在我死后戴上王冠坐上皇帝的宝座。”就这样，这驴子被养了下来，慢慢长大了，它的耳朵又细又长，向上直伸着。\n    驴儿天性活泼，到处跳跃、游戏，且非凡爱好音乐。于是他走到一位有名的乐师那里，说“把你的本领教给我吧，我要把琴弹得和你一样好。”“啊，小少爷，”乐师说，“这对你来说就难了，你的手指实在太粗，不是块弹琴的料。我怕琴弦经不起。”但他的推脱没用，驴儿要弹琴，非学弹琴不可。他学起来又勤快、又刻苦，最后练得竟和师傅一样好了。\n　　有一次，这小主人出去散步，脑子正在思考着什么，不觉来到了一口井边。他往水中一瞧，见水面清亮如镜，那儿有自己的驴子模样。他懊丧极了，便带了忠实的仆人离家出走，到了很远的地方。他们四处漂泊，最后来到了一个王国，统治这个国家的是位年迈的国王，不过他有一位漂亮绝伦的独身女。驴儿说：“我们就在这儿呆下吧!”说着就去敲城门，“外边有客，快开门让他进来!”有人说但是大门没有打开。他于是坐了下来，取出他的琴，伸出两只前腿弹起琴来，音乐非常美丽动听。守城门的人听得睁大了眼，赶忙跑去报告国王：“门外坐着头驴子，琴儿弹得有大师那么棒。”“让那位音乐家到我这儿来吧!”国王说。\n　　当那驴儿一进来，所有的人都称赞起这位弹琴的来，他们让他坐下来和仆人一块儿吃饭，他却很不乐意，说：“我可不是头普通的驴子，我可是位贵族。”他们说：“假如你真是位贵族，就和武士们坐一起吧。”“不，我要坐在国王身旁。”国王微微一笑，很幽默地说：“好吧，就照你的意思办。小毛驴，到我这儿来吧。”然后他又问：“小毛驴，你觉得我女儿怎么样?”驴儿转过头看着她，点了点头，说“实在太美了!我还没见过像她这样漂亮的女孩。”“那么，好吧，你也该挨着她坐吧!”国王说。“那我是求之不得的呢!”驴儿一边说，一边紧挨着公主坐下。他又吃又喝，既举止优雅，又注重清洁卫生。\n　　这高贵的驴儿在宫里住了一些时，他想：“这一切对我有何用呢?我得回家去。”于是他便难过地垂下了头，来到国王跟前，请求能让他走。但国王已经喜欢上他了，便说：“小毛驴，你什么事儿不开心?你看起来就像一缸醋一样酸溜溜的。你要什么我就给你什么，你要金子吗?”“不，”驴儿摇头说。“你要珠宝和华贵的服装吗?”“不。”“我分给你半个王国，好吗?”“啊呀，不。”于是，国王说：“什么能教你开心，你是不是想娶我的宝贝女儿做妻子?”“啊，是的是的。”他一下子变得兴奋起来，那确实是他所希望的。【儿童睡前故事：www.qigushi.com】\n　　于是他们举行了隆重而豪华的婚礼。新婚之夜，新郎和新娘被带进了洞房，国王想知道驴儿是不是举止温文尔雅，便命一个仆人躲在那里。他们双双走进了新房，新郎闩上了门，他以为只有他们俩在那里，只见他摔脱了身上的皮，站在那儿，竟是个英俊潇洒的青年。“现在你瞧我是谁，看我配不配得上你。”这时新娘非常兴奋，过来吻他，打心眼里爱着他。到了早上，他跳了起来，又重新披上驴皮，没有人会想得到里面藏着个多么英俊的帅小伙子。不久老国王来了，“啊，”他叫道，“驴儿已经起床了!女儿呀，你一定很伤心，没能嫁一个能真正作你丈夫的人。”“哦，不，亲爱的父亲，我很爱他，他就像是世上最英俊潇洒的小伙子，我会终生和他厮守在一起的。”国王感到很惊奇。但是先前藏在新房的仆人来了，向国王透露了一切。\n　　国王说：“这绝不可能是真的!”“那么今晚就请您亲自去看看，你会亲眼看见的。国王，你听我说，你去把他的皮拿走，把它扔进火里烧掉，到时他就会显露真形的。”国王说：“你这主意很不错。”到了晚上他们睡觉时，他偷偷地走了进去，来到床前，借助月光他看见了一个相貌堂堂的年轻人躺在床上，那件驴皮就放在地上。于是他把驴皮拿走了，让人在外面生起火，然后把驴皮扔了进去，等着看到它化为了灰烬。\n国王急于想看看这位丢了东西的新郎会怎样，便彻夜没睡，一直关注着。年轻人睡醒后，就着晨光一翻而起，想穿上那张驴皮，但没找着。这下他变得很惊恐焦虑，又非常伤心，说：“现在我非逃走不可了。”，但他刚走出来，但便发现国王正站在那儿。国王说：“我的儿，你这么匆匆忙忙上哪去?你心里有啥事?留在这里吧，你是个这样潇洒的小伙子，你是不会离开我的。我现在就把我的一半王国给你，等我死后，整个王国都归你。”“我也希望善始善终，那我就留在这儿吧!”\n随后老国王给了他一半的国土，一年后国王死了，整个王国都属于了他。他自己的父亲死后，他又得到了另一个王国，从此便过着荣华富贵的生活。";
    
    NSString *two = @"　　古时候，上帝在那时还习惯于亲自与地球上的凡人打交道。\n　　有一次天已经黑了，他还没有找到一家酒店，蒙蒙夜色使他身心憔悴。这时他发现前面有两栋房子面对面地竖立在路的两边：一栋大而漂亮，另一栋小而破旧，大的属于一个财主，小的属于一个穷人。\n　　上帝暗想：“如果我住在财主家，是不会给他增加负担的。”\n　　当财主听到有人敲门时，他打开窗户问陌生人想要什么，上帝回答：“我就想住一晚上。”\n　　财主上上下下将来人打量了一番，见上帝衣着平凡，不像兜里有什么钱的人，他摇摇脑袋说道：“不行，我不能让你住，我的屋子里堆满了草药和种子，如果凡是敲门的人我全接待的话，用不了多久，我就得出门要饭了。到别处找地方住吧。”说完，他关上窗户把上帝搁在了外边。\n　　于是上帝转身离开了财主，走到对面的小房子前敲门。刚刚敲了门，那穷人就打开了那扇小门并把来人请了进去。\n　　“留下同我一起过夜吧，天已经黑了，”他说：“今晚你不能再赶路了。”\n　　上帝被感动了，他走进屋来。\n　　穷人的老婆握着他的手表示欢迎，并让他别客气，就像到家一样，有什么就用什么，说虽然他们拿不出很多，可是他们会真心实意地用所有的东西招待他。女主人把土豆放在火上煮，同时又去挤羊奶，这样他们就有些奶喝了。铺上桌布后，上帝和主人两口子坐了下来，虽然饭菜不精美，可上帝很欣赏，因为大家都喜气洋洋地坐在餐桌旁用餐。\n　　晚饭后该上床睡觉了，女主人把她的丈夫叫到一旁说：“听着，亲爱的夫君，今天晚上咱们自己铺张稻草床吧，让那可怜的客人在咱们的床上好好睡一觉，他走了一整天，一定累了。”\n　　“我完全同意。”他答道，“我这就去告诉他。”\n　　他过去邀请这陌生的客人说，如果不嫌弃的话，就请睡在他们的床上好好地休息。可是上帝定然不肯睡在两位老人的床上。无论上帝如何拒绝，他们就是不同意，直到最后，上帝接受了，睡在了床上，他们自己在地上铺了些草躺在了上面。\n　　第二天，天刚亮，他们就起床为客人作了一顿他们所能作的最好的早餐。当阳光穿过了小小的窗户时，上帝起了床，又和他们一起吃了饭，然后准备起程赶路。\n　　他站在门前，回过身去说道：“你们是善良的人，请为自己许三个愿吧，我会恩准的。”\n　　于是穷人说：“我希望我们两口子一辈子幸福健康、每天都有面包吃，这第三个愿望么，我不知道还需要什么。”\n　　上帝对他说：“难道你不想用一座新房子替换你这旧房子吗？”\n　　“噢，对，”穷人道，“我非常高兴，如果我也能有座新房子的话。”\n　　上帝实现了他的愿望，将他们的破旧房子变成一座新房，然后再次向他们表示了祝福，便上了路。\n　　太阳高高升起了，财主起了床，从窗户探出身子向外望，看见路对面原来破旧小棚子的地方，出现了一栋崭新的红砖房，窗户很明亮。他不禁大吃一惊，忙把他的老婆叫来问道：“跟我说，出了什么事？昨晚上还是那个可怜巴巴的小棚子，今天怎么就成了一栋崭新漂亮的大屋子，赶紧过去看看那是怎么了。”\n　　于是他的老婆过去问穷人，他告诉她：“昨晚上，有个过路的来要求住一宿，今天早上走的时候让我们实现了三个愿——一辈子幸福健康和天天有面包，另外还用一栋崭新漂亮的大房子代替了我们的旧棚子。”\n　　富人的老婆听后，赶紧跑回来告诉她丈夫事情的经过。富人叹道：“我真恨不得撕了我自己！我怎么早不知道！那过路的先来的我们家，想在这儿借宿，是我把他轰走的。”\n　　“那你还不快点儿！”他老婆督促道：“骑马去追。你还能赶上他，你必须让他也让你实现三个愿。”\n　　富人觉得这主意不错，骑上马飞奔而去，一会儿就追上了上帝。他对上帝轻声细语地道歉，请上帝别因为没让他直接进屋而生气，说他当时是在找前门的钥匙，没想过路人已经走了；如果他还回来的话，他必然会让他住在一起。\n　　“好吧，”上帝说：“如果我还回来，我就这么做。”\n　　然后富人问他是否也能许三个愿，就像他的邻居一样。\n　　“行啊，”上帝回答。\n　　但是显然这对他可能没什么好处，他最好还是别许愿。可富人却暗想，只要你让我实现愿望，我就许愿让我的日子过得更舒心。\n　　上帝没有办法，只得告诉他：“回家去吧，过会儿你许的三个愿会实现的。”\n　　富人的要求得到了满足，在回家的路上，他一边骑着马，一边想他该许什么愿，想着想着，缰绳掉了，这时马便开始不老实走路了，边走边跳，搅乱了他的思维，使他根本无法集中思想。他拍拍马的脖子说：“轻点儿，丽萨。”\n　　可是那马又开始玩新花样。最后他实在忍不住了，大声吼道：“我希望摔断你的脖子！”话音刚落，那马立刻倒地，一动不动地死了。\n　　就这样，他的第一个愿望实现了。由于他生性吝啬，舍不得把马鞍子给扔了，所以他把马鞍子卸了下来，扛在肩头。现在他不得不走着回家了。\n　　“我还剩下两个愿望。”他自己安慰自己。www.qigushi.com 童话故事\n　　他在沙漠上缓慢地走着，中午的太阳跟火炉一样热，他的火气越来越大。马鞍硌着肩膀疼，他还没想出要许个什么愿。\n　　“如果我想得到世界上所有的财富，”他自言自语：“我肯定不会一次想全了，得事先都意料到，想方设法一次成功，不漏掉任何东西。”然后他叹了口气：“唉，我要是个巴伐利亚的农民的话，许三个愿是件很容易的事啦，第一个愿是要大量的啤酒，第二个愿是自己能喝多少就要多少啤酒，第三个愿是再多要一桶啤酒。”\n　　有好几次他觉得他已经想好了，可是过会儿，他又觉得太少啦。这时他脑子里想的是他老婆过得多舒服，呆在屋子里凉凉快快地，说不定正在吃什么好吃的。这么一想不要紧，自己就别提多恼火啦，糊里糊涂地说出：“我真希望她坐在这马鞍子上下不来，省得我一路上老扛着它了。”\n　　他话还未说完，肩上的马鞍子就没了，他这才明白第二个愿望也实现了。他立刻感到热得受不了啦。他开始跑了起来，想快点儿回到家中，一个人呆在屋子里好好地想些真正的大事可以许愿。谁知道等到了家，打开房门，他看见他老婆正骑在房子中间的马鞍上，又哭又闹，怎么也下不来。\n　　他安慰道：“忍受一会儿，等会儿我许愿把世间所有的财富都给你，你就呆在那儿别动。”\n　　然而，她却骂他是个傻瓜：“如果我老是骑在这马鞍子上下不来，那么世间的所有财富对我又有什么用？是你许愿把我给许上去的，你得给我弄下来。”\n　　这样一来，富人没有办法了，无论他愿意还是不愿意，他都不得不许第三个愿让他的老婆从马鞍子上下来。这个愿望也马上灵验了。\n　　最终，富人除了烦恼、劳累和羞辱，并且还损失了他的马外，一无所获；而那一对穷人却快乐，宁静，守本份地生活了一辈子。";
    
    
    NSString *three = @"　　山坡上，有一座漂亮的红房子，那是小灰兔的。山脚下，有一座美丽的绿房子，那是小白兔的。\n　　夏天到了，天气真热。但山坡上风儿徐徐吹，小灰兔住在红房子里，很凉爽。\n　　山脚下的小白兔热得不行。喝水，不管用；扇扇子，还是不管用。不管白天晚上，小白兔都热得睡不着觉。\n　　正当小白兔急得团团转时，小灰兔来了：“小白兔，请到红房子里和我一起住吧，我的房子里很凉爽!”\n　　“哦，谢谢你!”小白兔十分感激。\n　　小白兔住进了小灰兔的红房子里。整个夏天，他都在凉爽中度过。\n\n　　冬天到了，北风呼呼地吹，山坡上大风嗷嗷地叫着。小灰兔住在红房子里，穿着厚厚的衣服，盖着厚厚的被子，还是冷，整夜睡不着觉。\n　　第二天，小灰兔刚起床，小白兔来了：“小灰兔，请到山脚下我的绿房子里住吧，我的房子风吹不着，很暖和!”\n　　“好的，太谢谢你了!”小灰兔感激地说。儿童睡前故事大全\n　　小灰兔立即跟着小白兔来到了山脚下的绿房子里。\n　　第二天，美美地唾了一觉的小白兔和小灰兔醒来：“我把我的房子送一半给你!”他们同时说。两人对看一眼，笑了。\n　　“这样夏天的时候你就有凉爽的房子了!”小灰兔说。\n　　“这样冬天的时候你就有温暖的房子了!”小白兔说。\n　　山坡上，有一座漂亮的红房子，那是小灰兔和小白兔的。\n　　山脚下，有一座美丽的绿房子，那也是小灰兔和小白兔的。";
    
    
    NSString *four = @"　　花儿们要举行盛大的选美比赛，每一朵花都躲在自己的小房子里精心打扮。\n　　牡丹花的裙子多极了，她在她的小房子里穿了一层又一层，穿了一层又一层……\n　　玫瑰花躲在小房子里卷着每一条裙子的边儿，然后又往裙子上拼命洒香水……\n　　仙客来躲在小房子里精心裁剪时装，她可不像一般花几那样穿裙子，她要让自己的时装别具一格……\n　　水仙花躲在小房子里反复研究自己金色王冠配白裙子的效果，追求典雅是她向来的风格……\n　　好了，一切都已准备就绪，打扮得漂漂亮亮的花儿们走出了自己的小房子，她们个个信心十足……\n　　可是，有一朵小小花儿躲在自己的小房子里迟迟不肯出来，原来，她没有漂亮的衣服，她的全部家当就是一条既不鲜又不美的短裙，那么窄、那么小，算不上真正的裙子，围在身上一点儿也不好看。\n　　小小花儿听见外面的喧笑声，她从门缝里偷偷往外看，啊呀，罂粟花正摇曳着她那艳红镶白边儿的绸裙子跳西班牙舞，那华丽宽大的裙子翻飞着把小小花儿的眼睛都晃花了！\n　　小小花儿这下更不敢出去了。她关紧门，躲在房子里焦急地说：“怎么办？怎么办？”\n　　像是一片回声，周围都传来“怎么办？怎么办”的叹息。原来，小小花儿的姐妹们都同她一样胆怯地躲在房子里。\n　　“小姐姐，你带个头先出去怎么样？”隔壁传来花儿妹妹的声音。\n　　“是呀，是呀，你带个头先出去。”四周传来众妹妹的央求。\n　　这时，选美比赛马上就要开始了，蝴蝶评委、蜜蜂主席和特邀主持人金铃子小姐都已到来。所有花儿们都着急地催促躲在房子里的小小花儿们快出来。\n　　“怎么样？我们不出去会影响整个选美比赛，唔，这样吧，我喊一、二、三，我们同时出去。”小小花儿鼓足勇气说。妹妹们都赞成。\n　　小小花儿深深地吸了一口气，大喊了一声：，一、二、三！”\n　　刹那间，千千万万朵小小花儿铺天盖地缀满枝头。\n　　“哇，好美，好美的花儿呀！”蝴蝶、蜜蜂、金铃子和开有的花儿都惊呆了。\n　　小小花儿每一朵细看都不美，然而她们成干上万朵簇拥在一起形成的磅礁气势却是那么壮观。那种惊心动魄的美压倒了所有争奇斗艳的花儿。\n　　小小花儿奇迹般地获得了选美比赛的冠军。\n　　一切都不是小小花儿们躲在小房子里所能预料到的，她们庆幸自己勇敢地走出了小房子——不管自己是多么的平凡，只有走出了小房子，才能够去创造奇迹。";
    
    NSString *five = @"　　从前，有一个父亲把他的三个儿子叫到一起，给了大儿子一只公鸡，二儿子一把长柄镰刀，三儿子一只猫，然后说道：“我老了，快不行了，我死之前还得为你们预备点东西。钱我是没有的，现在我给你们的这些东西也不值钱，如何妥善地利用我的这些礼物来发迹，就取决于你们自己了，只要找到这样一个地方，那儿没有你们拥有的东西，你们的运气就来了。”\n　　父亲死了以后，大儿子带着公鸡出发了，但是他无论走到哪儿那儿都有公鸡。在每个城镇，很远他就看见教堂的尖塔上都站着一只公鸡随风转动（其实那不过是风向风速器，也就是风信鸡），各个乡村，公鸡的叫声到处都可以听到，他的鸡一点也不新奇。看来他发财的机会似乎很渺茫。\n　　但他并不灰心，继续不停地找，终于还是找到了这样一个地方。他到了一座岛上，岛上的人从来没有听到过鸡叫，也不知道如何计时，他们知道早晨或者晚上，但到了夜里睡觉时，就不知道时间过去了多少。\n　　他对他们说：“你们看吧！这是一只多么高贵的动物啊！它多么像一个骑士。瞧！它头戴鲜红的头盔，脚蹬锡马刺，每天晚上隔一段时间它就叫一次，第三次叫时，太阳不久就要升起来了。不仅这样，它还能预报天气，如果它在大白天叫，就是提醒你们天气要变了。”\n　　听了他的介绍，当地的人们非常高兴，他们整夜没有睡觉，怀着兴奋的心情，等着听这只公鸡的啼叫，每次鸡叫都使他们激动不已。鸡在两点、四点、六点共叫了三次。\n　　于是他们问他这鸡是不是卖，要卖多少钱，他说：“大约要一匹毛驴所能驮的金子。”\n　　他们齐声叫道：“这是一只高贵的动物，这个价钱很公正。”\n　　他们给了他所要的那些金子。\n　　当他带着所得的财富回到家里时，两个弟弟非常惊讶。老二说：“我现在也要出去，看是能否用这把长柄镰刀换来好财运。”可他出来一试，就觉得希望太小了。因为，不管他走到哪里，他都能遇到农夫肩上扛着和自己一样的长柄镰刀的情形。他并不气馁，跑了一个又一个地方。\n　　真是功夫不负有心人，他最终还是交上了好运：他也来到了一个岛上，那儿的人们连长柄镰刀的名字听都没听说过。不久麦子熟了，他们来到农田将麦子拔了起来，但这样收获非常劳累，而且许多麦子都掉下来浪费了。摘自七故事网 &nbsp;www.qigushi.com\n　　老二用他的长柄大镰刀来收割，很快就割完了全部的庄稼，人们张大嘴巴站在那儿，用惊奇的目光看着这一切，纷纷愿意出他所要的任何价钱来换取这把奇妙的工具，他要了一匹驮满金子的马回到了家。\n　　老三现在也想出去看看能用猫换取到什么东西，于是他出发了，开始他的命运也和前两个哥哥一样，跑了不少地方都没有成功，陆地上到处都有猫，真是太多了，以致许多刚出生的小猫都被扔进水里淹死了。\n　　最后他在海上寻找机会，终于在一个岛国如愿以偿。岛上的人从来没有看到过猫，那儿老鼠泛滥成灾，不管房屋的主人在家或不在家，这些讨厌的小家伙都在桌子上椅子上窜来跳去，人们叫苦不迭。国王在他的宫殿里也不知道该怎样办，每个角落里都有老鼠在吱吱叫个不停。凡是它们的牙齿能够咬的东西都被咬坏了。\n　　老三为猫在这里找到了用武之地，猫一下地，马上就开始捉老鼠，一转眼的功夫，就把两间房子里的老鼠清理完毕。这时人们恳求国王买下这只奇异的动物，用多少钱都行。国王乐意地满足了老三的要求，给了他一匹驮满了金子和宝石的骡子。因此，老三带着比他两个哥哥更值钱的财宝回到了家里。\n　　猫留在了岛上，它在王宫里捕捉老鼠，快活极了，追来追去，忙个不停，咬死的老鼠数也数不清。终于它累得精疲力竭，口也渴得冒烟，于是静静地站在那儿，伸着脖子仰着头叫了起来，“咪——，咪——”。\n　　听到这奇怪的叫声，国王把所有的大臣都召集起来了，看看采取什么措施最好。最后，他决定派一名传令官去见猫，警告猫立刻离开王宫大院，否则就要用武力来赶它出去。\n　　那些顾问说：“我们宁愿和老鼠共处，也不愿冒生命危险和这只猫共处。”\n　　一个小听差应命走了进去，他问猫：“你是否愿意离开王宫？”\n　　但这只猫口渴得更厉害了，一个劲地叫着“咪——，咪——”。\n　　这小听差以为猫是说“不！不！”所以就把这个意思告诉了国王。\n　　顾问们一听，马上说：“好吧！那我们就用武力来对付它吧。”\n　　他们架起火炮向王宫轰去，王宫四周燃起了大火，当火烧到猫待的那间房子时，它从窗口跳了出去，围攻的士兵都没有看到它。大火一直烧个不停，最后，整个宫殿都化成了灰烬。";
    
    NSString *six = @"　　从前，有一位国王，膝下有一个女儿，美丽非凡，却因此而傲慢无理，目中无人，求婚的人里没有谁中她的意。她不但一个接一个地拒绝他们的美意，而且还对人家冷嘲热讽。\n　　有一回，国王举行盛大宴会，邀请了各地所有希望结婚的男子。先入席的是几个国王，接着入席的是公爵、伯爵和男爵，最后入席的是其余所有应邀而来男子。\n　　公主走过这个行列，可对每一位横挑鼻子竖挑眼，这位太胖啦，她就用轻蔑的口气说道：“好一个啤酒桶。”那个呢，又高又瘦，她就评头论足地说道：“活像一只大蚊子。”下一个呢，太矮啦……“五大三粗，笨手笨脚。”她又说道。第四个呢，脸色太苍白啦，“一具死尸。”；第五个，脸太红润……“一只公火鸡。”第六个呢，身板儿不够直……“像一快放在炉子后面烤干的弯木头。”就这样，她看谁都不顺眼。\n　　有一位国王，下巴长得有点儿翘，更是免不了遭到她的大肆嘲笑挖苦。“我的天哪！” 她一边放声大笑一边高声地说，“瞧这家伙的下巴呀，长得跟画眉嘴一模一样啊！”\n　　打那以后，这位国王就落了个诨名——画眉嘴。老国王发现女儿只是在嘲弄人家，对每个前来求婚的人都嗤之以鼻，便大动肝火，发誓要把她嫁给第一个上门来讨饭的叫花子。\n　　几天以后，一个走街窜巷卖唱的人在王宫的窗下唱起歌来，想讨一点儿施舍。国王听见了歌声，便吩咐把这个人带来见他。卖唱的衣衫褴褛，肮脏龌龊，来到国王和公主面前唱了起来，唱完便恳求给他一点儿赏赐。\n　　国王对他说：“你的歌让我很开心，我就把我的女儿许配给你吧。”\n　　公主一听，吓得浑身发抖，国王却接着说：“我发过誓，要把她嫁给第一个到这儿来讨饭的叫花子，我得言而有信。”\n　　抗旨不遵完全是徒劳的。于是，请来了牧师，为公主和这个走街窜巷卖唱的人举行了婚礼。\n　　婚礼结束后，国王说道：“现在你已是一个叫花子的老婆了，不宜再留宫中。你和你丈夫快上路吧。”\n　　叫花子牵着她的手往外就走，公主不得不跟着他离开了王宫。他们俩来到一片大树林前面，公主问：“这片树林是谁的？”\n　　卖唱的便回答道：“是那位心地善良的画眉嘴国王的呀，要是你当初嫁给他，现在不就是你的吗？”\n　　公主听了回答说：“我这个可怜的女孩子啊，当初有点儿翘尾巴，要是嫁给画眉嘴国王就好啦。”\n　　随后，他们俩来到一片绿草地，公主又问：“这片美丽的绿草地是谁的？”\n　　“是那位心地善良的画眉嘴国王的呀，要是你当初嫁给他，现在不就是你的吗？”\n　　于是，公主又唉声叹气地说：“我这个可怜的女孩子啊，当初有点儿翘尾巴，要是嫁给画眉嘴国王就好啦。”\n　　接着，他们俩来到一座大城市，公主又问：“这座美丽的城市是谁的？”\n　　“是那位心地善良的画眉嘴国王的呀，要是你当初嫁给他，现在不就是你的吗？”\n　　公主听了说：“我这个可怜的女孩子啊，当初有点儿翘尾巴，要是嫁给画眉嘴国王该多好啦。”\n　　“你老是渴望嫁给另一个男人，”卖唱的说，“我听了真气愤。难道我配不上你吗？”\n　　最后，他们俩来到一所很小的房子前，她大声地问：“这么小的房子我还没见过，天哪，它会是什么人的窝？”\n　　卖唱的回答说：“这是我的房子，也是你的家，我们就共同生活在这里。”\n　　房门又矮又小，公主进去时，不得不弯下腰来，不然就会碰了头。\n　　“佣人在哪儿呢？”公主问道。\n　　“哪来的佣人呀。”叫花子回答说，“干什么事你都得自己动手。喏，你得快点儿把火生起来，把水烧开，然后给我煮饭。我已经累得不行了。”\n　　可是，公主哪里会生火煮饭呀，叫花子只得自己动手，不然就得挨饿。他们的晚饭很简单，晚饭后，就休息了。谁知第二天一大早，他就把她赶下床，逼着她做家务事。\n　　他们就这样过了几天，吃完了所有的存粮，丈夫于是说：“老婆，你看，咱们这样光吃饭，不挣钱，可怎么活下去呀，你来编筐子吧。”\n　　说罢，他就出去砍了些柳枝，扛回家来。公主开始编筐子，可柳枝又粗又硬，把她娇嫩的双手全弄伤了。\n　　“我觉得，”丈夫说，“这样不行啊，别编筐子啦，你还是纺线吧，也许你会在行些。”\n　　于是，她开始坐下来试着纺线，可是纱线很粗糙，把她柔软的手指勒得鲜血直流。\n　　“你看看，”丈夫又说道，“这算怎么一回事嘛。你什么也干不了，娶了你当老婆，我算倒霉透啦。现在我得做一做陶器生意，卖锅碗瓢盆什么的。你呢，得到市场上去叫卖。”\n　　“天哪，”她心想，“要是我父亲王国里的人来赶集，看到我在那儿叫卖锅碗瓢盆，他们一定会嘲笑我的！”\n　　可是，又有什么别的出路呢？不然就得活活饿死。一开始，她的生意还不错。人们见她长得漂亮，都来买她的东西，而且连价也不还。的确，有几个人付了钱，却又把锅子作为礼物送给她。\n　　夫妻俩靠她卖来的钱生活了一段时间，然后丈夫又进了一批陶器。她坐在市场的一个角落里，把锅碗瓢盆什么的摆放在自己的周围，叫卖起来。谁知一个喝得醉熏熏的骑兵突然打这儿急驰而过，那匹马冲进她的货摊，把所有的陶器踩得粉碎。公主放声大哭，束手无策。 “我的天呀，我该怎么办哪？”她呜咽着说，“我丈夫会怎么骂我呀。”于是，她跑回家里，跟丈夫说了自己的遭遇。\n　　“你是一个卖陶器的小贩子，哭管什么用，”她丈夫说，“你什么活儿也干不了。我只得跑到咱们国王的宫殿里，打听了一下你能不能在那儿当个帮厨女佣。人家答应先试用一段时间，还有，你在那里可以白吃饭。”\n　　这样一来，公主就变成了帮厨女佣。她给大师傅打下手，干各种最脏的活儿。她在衣服里缝了一个口袋，在口袋里放了一只带盖的罐子，每天把残羹剩饭盛在里面，带回家中糊口。\n　　为了庆祝国王的长子满十八岁，国王举行了盛大的舞会。在那个不同寻常的夜晚，可怜的年轻女佣躲在上面大厅的门后，偷偷地观望。她目睹着蜡烛一根根点燃，宾客们一个个步入大厅，全都衣着华丽，光彩照人。面对眼前富丽堂皇、令人眼花缭乱的景象，她不无哀伤地想起自己悲惨的命运，站在那里几乎泣不成声。自己一向傲慢无理，目中无人，才落到今天这般贫穷凄惨的境地，她感到痛悔不已。美味佳肴端进端出，香味扑鼻，她馋得口水直流，仆人们不时扔给她一些残渣剩菜，她便装进罐子里，准备带回家去。\n　　国王的长子身着天鹅绒和绸缎衣服，衣服上镶嵌着钻石，脖子上挂着金项链，正朝大厅走去，发现这个可怜的女子站在门后，正偷偷地观望着舞会的情景，王子一把抓住她的手，要和她跳舞，她却不肯。她认出这位王子正是曾经向她求过婚，被她嘲弄侮辱过的那个画眉嘴国王，不禁吓得浑身发抖。可是，不管她怎样挣扎，王子还是硬将她拉进了舞厅。不料，她用来系口袋的线绳，就在这时断了，罐子一下子滚了出来，汤汤水水流了一地，残渣剩菜撒得到处都是。人们一见哄堂大笑，她成了众人的笑柄，羞愧得恨不得有个地缝钻进去。\n　　她朝门口冲了过去，想要逃走，可在台阶上被一个男子拦住了去路，又给拉了回来。她定睛一看，这个男子又是画眉嘴国王，国王用亲切和蔼的语气对她说：“别怕，我和那个跟你生活在破破烂烂的小房子里的叫花子，原本是一个人哪。我很爱你，才乔装打扮成叫花子；那个喝得醉熏熏的、冲进你的货摊，把陶器踩得粉碎的骑兵，也是我呀。我做这些，全是为了克服你的傲慢无礼，惩罚你对新郎的嘲弄。”\n　　公主听罢，痛哭流涕，抽泣着对国王说：“我真是太不应该了，不配做您的妻子。”\n　　画眉嘴国王却安慰她说：“过去的已经过去了。现在我们就举行婚礼吧。”\n　　话音刚落，宫女们随即走了过来，给她打扮得花枝招展。她父亲和宫里的人也来了，祝贺她和画眉嘴国王新婚幸福。";
    
    NSString *seven = @"　　小镇上，住着一位专门做玩具的薛贝特老伯伯，老伯伯没有孩子，生活非常寂寞，因此，有一天，老伯伯便用木头做了一个可爱的小木偶男孩。\n　　做完后，老伯伯对他爱不释手，忍不住叹口气说：\n　　唉!如果你是个真的小男孩，那该有多好啊!\n　　半夜里，怪事发生了，看啊!一位美丽的天使突然出现了。\n　　仙女轻轻对熟睡的老伯伯说：\n　　老伯伯，因为你曾经做过许多美丽可爱的玩具，为孩子们带来欢笑，因此，我就帮您将你的愿望实现。\n　　仙棒轻轻一点，小木偶站起来了。\n　　小木偶在桌子练习走路，走着，走着，竟跌倒了，吵醒了老伯伯。\n　　老伯伯揉揉双眼，不敢置信，最后，终於忍不住，高兴的一把抱起小木偶，又唱又跳!\n　　第二天，小木偶开始上学了。他带着书往学校去。\n　　才走到半途，忽然听到喇叭声、鼓声.，好不热闹!\n　　咦，是在演戏呢!真想去看看可是，又必须上学，唉!我想!迟一天去上学，应该是无所谓的。 这麽安慰自己后，小木偶便急急往戏团去了。\n　　戏团的主人因为欣赏小木偶，便赏了他五枚金币。小木偶高兴极了，拿着金币往家里跑，谁知竟碰上了狡猾的狐狸和猫。\n　　小木偶啊!你想不想让你的钱变的更多呢?我们知道有个奇异的草原，只要你种下一个金币，就会生一棵长满金币的树哦!\n　　真的，请你们快带我去吧!\n　　结果，小木偶受骗了，他们逼他交出金币来，小木偶将金币藏在嘴里，狐狸及猫找不到，便将他吊到树上去了。\n　　就在小木偶逐渐支持不住时，仙女来了，救了他。\n　　到底是怎麽回事呢? 仙女问。\n　　说实话太丢脸了。 小木偶心想，於是，开始胡扯。\n　　咦，不对呀!怎麽愈说鼻子变得愈长呢?小木偶继续说，鼻子又继续变长，他终於忍不住哭出来了。\n　　救救我，我再也不敢说谎了。 小木偶求仙女。\n　　仙女这才让他的鼻子恢复正常。\n　　这一次，小木偶决心要做个好孩子了。\n　　可是，同伴们又引诱他到欢乐国去。小木偶禁不惑，又心动了。\n　　欢乐国里全部都是小孩子，每天不必上学，也不必工作，只是玩，真是十分快乐。小木偶已将爸爸及仙女的事全忘光了。\n　　可是，有一天，不幸的事发生了，小木偶发现自己变成了一只驴子。\n　　原来，这欢乐国就是坏人所设计，专门用来引诱贪玩的小孩，好将他们变成驴子出售的。\n　　坏人将小木偶和其他的驴子一起赶上船，走到甲板上，小木偶突然 扑通! 一声跌到海里去了。\n　　跌到海里的小木偶，逐渐恢复了人形。小木偶好高兴，赶紧朝着岸边游去。\n　　突然， 哗啦! 一声，前面出现了一只大鲸鱼，嘴一张，便将小木偶吞了下去。\n　　鲸鱼的肚子里，漆黑一片。\n　　不前方怎麽会有亮光呢?小木偶靠近一看.啊!那不是爸爸吗?\n　　父子两人相拥而泣。\n　　原来，薛贝特老伯伯是为了寻找小木偶，驾船到海上，才被鲸鱼吞下去的。\n　　爸爸，我有个逃出去的好办法。鲸鱼感冒了，常常打喷嚏，我们可以趁机逃到外面去。\n　　小木偶算好时间，顺利地带着爸爸逃了出来。\n　　薛贝特老伯伯和小木偶在冰冷的波浪里向前游，最后，薛贝特终於体力不支，小木偶不顾一切地拖着他，奋力朝岸边游.\n　　上岸了，小木偶也精疲力尽地昏倒.\n　　等到他醒来， 看啊!爸爸，我变成一个真的小孩子了!\n　　是的，这便是仙女给他的最大奖赏。";
    
    NSString *eight = @"　　一只小老鼠费尽心机，冒着生命危险，偷来一块牛奶糖。\n　　他最爱吃糖，自称糖老鼠。\n　　当糖老鼠正准备美美地享受一番时，他大吃一惊--牛奶糖的包装纸上也有一只老鼠！\n　　“人最怕我们老鼠偷吃他们的东西，怎么把个老鼠放在糖纸上？”糖老鼠纳闷了。\n　　“喂，老弟，你不喜欢吃糖？”糖老鼠对这位同胞的自制力表示惊讶。\n　　“我是米老鼠。”糖纸上的老鼠说。\n　　“噢，米老鼠可能爱吃米。”糖老鼠想。\n　　“那我可不客气了。”糖老鼠说完三口两口把牛奶糖吞了。\n　　吃完糖，糖老鼠抹抹嘴，还想吃：“喂，你是怎么住在这块糖上的？”“在糖果厂住上的。”米老鼠说，糖果厂里还有我的许多弟兄，每只米老鼠住一块糖。“我去行吗？”糖老鼠脑子一转，要是天天住在糖堆里，那日子，该多甜呀！\n　　“你这模样不行，人家一眼就能发现你不是米老鼠。”“那要是我化装一下呢？”“你试试。”糖老鼠照着米老鼠的样子化装了一番，还挺像。\n　　化装成米老鼠的糖老鼠出发去糖果厂了。\n　　◇ 二 ◇\n　　来到糖果厂，糖老鼠混在米老鼠群里，没被工人发现。\n　　糖老鼠也分到了一张漂亮的玻璃糖纸。\n　　朝思暮想的时刻到了，糖老鼠得到了一块牛奶糖。他使劲儿咽着口水。在车间里他没有轻举妄动：被人发觉了可不是闹着玩的。\n　　糖老鼠跟着牛奶糖刚一进仓库，便迫不及待地从糖纸上跳下来，扛起牛奶糖就跑。\n　　糖老鼠跑回家，告诉他的伙伴们，他可以天天吃糖了，还动员伙伴们和他一起去装扮米老鼠。\n　　能天天吃糖！老鼠们一传十，十传百。转眼间，成千上万只老鼠化装成米老鼠，跟着糖老鼠，浩浩荡荡地向糖果厂进军了。\n　　没过几天，糖果厂就发现牛奶糖大批丢失：仓库的地上净是空白的糖纸，上边的米老鼠也不见了！\n　　“一定是混进了假米老鼠。”工程师说。\n　　“怎么办呢？”经理慌了，工厂的损失太大了。\n　　“养一只猫吧！让猫来鉴别真假米老鼠。”工程师点子多。\n　　经理大喜，命令人立即找一只猫来。买也行。\n　　于是，糖果厂花大价钱买了一只花猫。花猫进厂那天，受到了全厂职工的隆重欢迎。大家把她看成是救星--丢失糖果的经济损失，影响大家的福利了。\n　　厂方先款待了花猫一顿红烧鱼。以后，不断有人给花猫送好吃的。\n　　◇ 三 ◇\n　　花猫忠实于自己的主人，她每天都从米老鼠群里抓出不少假米老鼠，而且从来没冤枉过真米老鼠。\n　　假米老鼠们恨死了花猫，不管他们化装得多像，也躲不过花猫的眼睛。\n　　假米老鼠越抓越少，牛奶糖也不大丢了。终于，假米老鼠全抓光了，牛奶糖也一块不丢了。\n　　花猫等待着主人重重地赏她。\n　　这天上午，花猫蜷缩在经理的办公桌底下睡觉。经理和工程师的对话吵醒了她。\n　　“现在糖果已经不丢了。”工程师说。\n　　“很好。”经理笑了。\n　　“花猫已经没用了。她每天要吃一元钱的食物，十天就是十元，一年就是三百六十元，十年就是。……”“哎呀，这给厂里造成的经济损失太大了！”“明天把她卖了吧！”“扔了也行。”花猫傻眼了，她不想离开糖果厂。\n　　怎么办呢？趁经理他们不注意，花猫回到自己的窝里。她把十几只假米老鼠关在笼子里，作为玩艺儿，没事儿时逗着玩。\n　　花猫打开笼子门，请假米老鼠们出来。\n　　假米老鼠不敢出来。\n　　“快出来！谁不出来我吃了谁！”花猫急了，快去仓库，去吃糖！越多越好！假米老鼠们不相信自己的耳朵。那只糖老鼠也在笼子里，他胆子大，带头朝仓库跑去。其他老鼠跟在后面。\n　　糖老鼠他们帮了花猫的忙--糖果厂又开始丢糖了。花猫不能走！\n　　◇ 四 ◇\n　　这回花猫有经验了，她把假米老鼠控制在一定数量之内。\n　　为了保持平衡，她还抓了不少真米老鼠呢！\n　　每当花猫馋了时，就让假米老鼠的数量多一些，这样厂方就重视她了，给她好吃的，哄着她。吃够了油水，花猫再把假米老鼠的数量减少一些，让主人知道没白喂她好吃的。花猫呢，顺便消化油水。\n　　后来，假米老鼠们一只只吃得肥头大耳。\n　　花猫也吃得大腹便便。\n　　年终糖果厂授奖时，花猫数居然最多。\n　　糖老鼠有点儿不服气，他觉得花猫的功劳也有他一份儿。";
    
    NSString *nine = @"　　美丽的蜗牛城，街道纵横，高楼林立。由蜗牛总督亲自担任队长的“蜗牛救火队”最近成立了。\n　　蜗牛救火队的装备是一流的，有超音速救火车、全自动直升机、智能灭火枪，还有伸缩自如的火场天梯。\n　　一天，城东一幢“刺破天大厦”着了火。\n　　蜗牛救火队接到火警，立即在蜗牛总督的率领下，向火场进发。\n　　蜗牛总督对灭火工作有很严格的规定，比如救火队员每分钟走多少厘米，水管喷水每分钟多少滴，都得按规定进行，不准太快太多，超速超数的要处罚。更有趣的是，连火焰蔓延的时间和速度也作了具体的限制。\n　　现在，蜗牛救火队正是按总督大人的要求，慢腾腾地开着超音速救火车，在半路上爬行。\n　　“刺破天大厦”已经烟雾滚滚，越烧越起劲——火的脾气十分暴躁而且无拘无束。总督的规定，大火根本不放在眼里。\n　　超音速救火车不能按自己的速度赶赴火场，心里窝着一把火，见大火已烧红了半边天，也顾不得总督大人的规定，开足马力准备急驶。总督却命令减速：“我们要按蜗牛的规矩办事，不能性急。”\n　　超音速救火车不肯停下，拉响了全速前进的汽笛，总督大人不愿违背自己的规定，一声令下，叫全体队员下车，徒步前进，并厉声对超音速救火车喝道：“你跑吧，回来我狠狠处罚你!”\n　　蜗牛救火队员们还没爬完一半路，超音速救火车和直升飞机密切配合，很快扑灭了“刺破天大厦”的火灾。\n　　回到总部，蜗牛总督召开了嘉奖和处罚大会，他宣布：“刺破天大厦这场火灾燃烧太快，我要给火一个严厉的处罚；超音速救火车和直升飞机超速行动，立即监禁；所有救火队员虽然还没到达火场，但它们严格执行我作的规定，给予最高荣誉，每位发给一枚金质奖章。”\n　　大会进入高潮时，火警又响了，位于城市中心的几座大楼同时着火。总督决定留在总部指挥，救火队在副队长的带领下开始爬行前进——因为超音速救火车和直升飞机在半分钟前已被监禁。\n　　火势凶猛，总督大人一再通过广播向火发出警告，要它们蔓延不要太快，火舌不能舔得太高，但火焰一点儿也不理睬，毫不客气地向四周迅猛延伸。\n　　蜗牛救火队按照标准步伐，半厘米半厘米操着正步。浓烟向它们卷来了，队员们戴起防毒面具，不时把头缩进硬壳里，躲避烈火的熏烤。\n　　美丽的蜗牛城已是一片火海，火舌已经舔到了总督大人的座椅底下，它不得不宣布解除直升飞机的监禁，急忙爬上机座，逃出城外。\n　　当蜗牛救火队到达起火地点时，眼前已是一片废墟。队员们例行公事地射水喷药，烧焦的楼房冒出一股股白色的水蒸汽。\n　　副队长拿起步话机：“报告，总督大人，城中心的火已扑灭。”\n　　“混蛋，现在整个蜗牛城快烧成黑炭了，你们怎么还在起火地点磨蹭!”总督在直升飞机上咆哮。\n　　“总督大人，我们完全是按你规定的速度赶到现场的。”副队长被浓烟呛得眼泪直流。\n　　蜗牛总督气得两条触角不停地颤抖。直升飞机在热烘烘的蜗牛城上空艰难地盘旋。这时，步话机传来副队长的哀叫：“总督大人，我们是蜗牛救火队，我们已被大火包围，请立即降落，救我们逃出火海!快!快!”\n　　蜗牛总督驾着直升飞机，来回兜着圈子，找不到一块安全着陆的地点。\n　　“快……救救……我们……”总督听到了蜗牛救火队最后的呼喊，眼泪哗地流下来。";
    
    NSString *ten = @"　　小乌龟强强非常强壮,动物们经常让他来帮大家干一些累活。\n　　有一天，一只小白兔要搬家，它一个人怎么也搬不动家具。这时，它想到了小乌龟强强。它找到强强，对它说：“强强，可不可以帮我搬东西啊？”强强说：“当然可以！”于是它跟着小白兔到了家，帮它搬东西。很快就到了小白兔的新家，小乌龟强强把东西放了下来，小白兔让强强坐下，拿来了毛巾给强强擦汗，还不停地感谢它。坐了一会儿，小乌龟就又去帮助别人了。一天下来强强听了很多夸自己的话，觉得自己很了不起，大家没有它就无法生活，小乌龟强强就开始骄傲起来了，在动物面前经常说自己能干。慢慢地，小乌龟觉得自己比狮子强，决定找狮子单挑。\n　　可是，小乌龟爬得太慢了，它抬头一望，看到了正在飞翔的大雁。于是，它叫住了大雁，傲慢地说：“我要向狮子挑战，你们快带我走。”大雁们听了，非常生气，决定教训一下乌龟。于是，它们找来一根树枝对它说：“你用嘴巴咬住这根树枝，我们带你走，不过你不能说话，不然你就会掉下去。“小乌龟说：”好好好，没问题。“它们起飞了，下面的小动物看见了会飞的乌龟纷纷夸道。小乌龟看到了非常得意，早就忘了大雁的话，兴奋地问：“我是不是很酷啊？”这一说，小乌龟掉了下去，乌龟摔了个粉碎。\n　　从此以后小乌龟再也不敢骄傲了。";
    
    NSString *shiyi = @"　　嘘，告诉你一个秘密。\n　　在草原上，有一群神奇的狐狸。他们只要闭上眼睛，轻轻地念：“叽叽叽——哩哩哩！”一个有趣的愿望就会钻进脑袋里。\n　　这可是一种了不起的魔力！\n　　今天，狐狸希希就要试一试这句咒语。\n　　“叽叽叽——哩哩哩！”\n　　一瞬间，希希有了一个心愿：他要把那座又破又旧的老风车变得像彩虹一样美丽。\n　　哈哈，一般的狐狸可想不出这么好玩的主意。\n　　希希越过一座山、翻过一个坡、钻进一个洞、爬上一棵树、游过一条小河、跳过一条小溪、穿过一片草地，采到了一朵七色花。\n　　然后，希希把七色花的花瓣搅成汁，用大刷子把汁液涂在老风车的叶片上。\n　　啊，又破又旧的老风车一下子变成了彩虹风车！\n　　风一吹，彩虹风车就会转动着七片美丽的叶片，轱辘轱辘地唱起歌，讲着很久很久以前的故事，多么神奇！\n　　这时，希希的心就会变得非常快乐、非常甜蜜！\n　　如果你也希望拥有一个有趣的愿望，就快像狐狸希希那样，闭上眼睛，轻轻地念：\n　　“叽叽叽——哩哩哩！”";
    NSString *shier = @"　　从前，有个国王整天被忧虑困扰。他担心自己的军队吃败仗，害怕王宫的珍宝被抢劫，怀疑大臣们不忠心……总之，从登基那时起，他就没过上一天舒坦日子。王宫外是个集市，从宫殿顶层可以看到赶集的人群。一天，国王望着集市上熙熙攘攘的老百姓，心想：他们是不是也像我这般不快活？真难想象普通人靠什么得到快乐。他让侍从找来最邋遢破旧的衣服，扮成乞丐，打算去王宫外看个究竟。国王沿着城墙走了大半天，傍晚时他来到了郊外的一座破旧的农舍前。农舍的主人正坐在昏暗的厨房里，吃着一小块面包，他已是暮年，但笑容却灿烂无比。国王忍不住走进去问他： 你为什么这么快乐？  我是个木匠，今天赚了足够的钱，晚饭有了着落，当然开心了。  如果明天没人找你干活，你还会开心吗？ 国王问。老木匠注意到面前的 乞丐 带着一脸焦虑和疲惫，便微笑着说道： 快乐和不快乐都是自己决定的，跟别人没关系。 说完，他把面包切成两半，将一半分给了 乞丐 。晚上，国王回到宫殿，对木匠的话越想越怀疑： 快乐怎么能由自己决定呢？我非要考验考验他，看他能快乐多久。 于是国王连夜颁布一条法令——所有住在城里的木匠必须到王宫门口站一个月的岗。国王并不是暴君，所以他规定站岗是有酬劳的，但要等到月末才一次性付清。第二天早上，老木匠还没出门就被侍卫长抓到宫墙外站岗，直到黄昏才放他回家。晚饭时间到了，国王急忙换上乞丐的装束，去木匠家探访，他边走边得意地想：看你还怎么快乐！谁知到了木匠家，国王看见桌上不仅摆放着面包，竟然还有葡萄酒。老木匠热情地请昨天认识的 乞丐 共进晚餐。国王好奇地问： 你今天的晚餐怎么如此丰盛？ 木匠笑着说： 我奉命去给国王站岗，要到月末才能拿到酬劳，所以我刚才去当铺，把侍卫长发给我的佩剑当掉了。你瞧，咱们现在不仅有面包，还有酒喝，多高兴啊！  这可是要杀头的啊！ 国王故意惊叫道。 没关系，一发工钱我就把剑赎回来，过会儿我用木头做把假的放在剑鞘里，保证没人能看出来。 木匠胸有成竹地说。第三天早上，国王乔装来到王宫大门口，果然看见木匠的 佩剑 插在剑鞘里，看上去跟真的一模一样。正在这时，对面一阵骚动，有个乞丐偷了小贩的甜瓜，正好被侍卫长抓住，集市上的人都跟过来看热闹。侍卫长严厉地说： 偷盗的惩罚是砍手。你， 他冲正在站岗的木匠招了招手， 用你的佩剑把小偷的右手砍掉。乞丐苦苦哀求道： 我饿得没办法才这么做的，饶了我吧。 木匠的处境可真糟糕，首先他很同情乞丐，另外他的 佩剑 一旦拔出来就会露馅儿，连国王都替他捏一把汗。就在这时，木匠仰头对天空大声说： 神啊，如果这个人罪不可赦，请赐予我执行命令的力量；如果这个人值得宽恕，请把我的铁剑变成木头的！说完他猛地抽出了剑。围观的人群发出阵阵惊呼： 变成木头的了！神仙显灵了！ 凶残的侍卫长不得不把乞丐释放了。国王走到木匠身边问： 你认得我吗？ 木匠看了他一眼，回答： 你是昨天跟我一起吃晚饭的那个朋友。 国王高兴地说： 从今以后请每天都与我共进晚餐。从此，木匠成了国王最器重的大臣之一。";
    NSString *shisan = @"　　从前有只老山羊。它生了七只小山羊，并且像所有母亲爱孩子一样爱它们。一天，它要到森林里去取食物，便把七个孩子全叫过来，对它们说： 亲爱的孩子们，我要到森林里去一下，你们一定要提防狼。要是让狼进屋，它会把你们全部吃掉的连皮带毛通通吃光。这个坏蛋常常把自己化装成别的样子，但是，你们只要一听到他那粗哑的声音、一看到它那黑黑的爪子，就能认出它来。 小山羊们说： 好妈妈，我们会当心的。你去吧，不用担心。 老山羊咩咩地叫了几声，便放心地去了。\n　　没过多久，有人敲门，而且大声说： 开门哪，我的好孩子。你们的妈妈回来了，还给你们每个人带来了一点东西。 可是，小山羊们听到粗哑的声音，立刻知道是狼来了。 我们不开门， 它们大声说， 你不是我们的妈妈。我们的妈妈说话时声音又软又好听，而你的声音非常粗哑，你是狼！ 于是，狼跑到杂货商那里，买了一大块白垩土，吃了下去，结果嗓子变细了。然后它又回来敲山羊家的门，喊道： 开门哪，我的好孩子。你们的妈妈回来了，给你们每个人都带了点东西。 可是狼把它的黑爪子搭在了窗户上，小山羊们看到黑爪子便一起叫道： 我们不开门。我们的妈妈没有你这样的黑爪子。你是狼！ 于是狼跑到面包师那里，对他说： 我的脚受了点伤，给我用面团揉一揉。 等面包师用面团给它揉过之后，狼又跑到磨坊主那里，对他说： 在我的脚上洒点白面粉。 磨坊主想： 狼肯定是想去骗什么人 ，便拒绝了它的要求。可是狼说： 要是你不给我洒面粉，我就把你吃掉。 磨坊主害怕了，只好洒了点面粉，把狼的爪子弄成了白色。人就是这个德行！\n　　这个坏蛋第三次跑到山羊家，一面敲门一面说： 开门哪，孩子们。你们的好妈妈回来了，还从森林里给你们每个人带回来一些东西。 小山羊们叫道： 你先把脚给我们看看，好让我们知道你是不是我们的妈妈。 狼把爪子伸进窗户，小山羊们看到爪子是白的，便相信它说的是真话，打开了屋门。然而进来的是狼！小山羊们吓坏了，一个个都想躲起来。第一只小山羊跳到了桌子下，第二只钻进了被子，第三只躲到了炉子里，第四只跑进了厨房，第五只藏在柜子里，第六只挤在洗脸盆下，第七只爬进了钟盒里。狼把它们一个个都找了出来，毫不客气地把它们全都吞进了肚子。只有躲在钟盒里的那只最小的山羊没有被狼发现。狼吃饱了之后，心满意足地离开了山羊家，来到绿草地上的一棵大树下，躺下身子开始呼呼大睡起来。\n　　没过多久，老山羊从森林里回来了。啊！它都看到了些什么呀！屋门敞开着，桌子、椅子和凳子倒在地上，洗脸盆摔成了碎片，被子和枕头掉到了地上。它找它的孩子，可哪里也找不到。它一个个地叫它们的名字，可是没有一个出来答应它。最后，当它叫到最小的山羊的名字时，一个细细的声音喊叫道： 好妈妈，我在钟盒里。 老山羊把它抱了出来，它告诉妈妈狼来过了，并且把哥哥姐姐们都吃掉了。大家可以想象出老山羊失去孩子后哭得多么伤心！\n　　老山羊最后伤心地哭着走了出去，最小的山羊也跟着跑了出去。当它们来到草地上时，狼还躺在大树下睡觉，呼噜声震得树枝直抖。老山羊从前后左右打量着狼，看到那家伙鼓得老高的肚子里有什么东西在动个不停。 天哪， 它说， 我的那些被它吞进肚子里当晚餐的可怜的孩子，难道它们还活着吗？ 最小的山羊跑回家，拿来了剪刀和针线。老山羊剪开那恶魔的肚子，刚剪了第一刀，一只小羊就把头探了出来。它继续剪下去，六只小羊一个个都跳了出来，全都活着，而且一点也没有受伤，因为那贪婪的坏蛋是把它们整个吞下去的。这是多么令人开心的事啊！它们拥抱自己的妈妈，像当新娘的裁缝一样高兴得又蹦又跳。可是羊妈妈说： 你们去找些大石头来。我们趁这坏蛋还没有醒过来，把石头装到它的肚子里去。 七只小山羊飞快地拖来很多石头，拼命地往狼肚子里塞；然后山羊妈妈飞快地把狼肚皮缝好，结果狼一点也没有发觉，它根本都没有动弹。\n　　狼终于睡醒了。它站起身，想到井边去喝水，因为肚子里装着的石头使它口渴得要死。可它刚一迈脚，肚子里的石头便互相碰撞，发出哗啦哗啦的响声。它叫道： 是什么东西，在碰撞我的骨头？我以为是六只小羊，可怎么感觉像是石头？\n　　它到了井边，弯腰去喝水，可沉重的石头压得它掉进了井里，淹死了。七只小山羊看到后，全跑到这里来叫道： 狼死了！狼死了！ 它们高兴地和妈妈一起围着水井跳起舞来。";
    
    NSString *shisi = @"　　夏季一个阳光明媚的早晨，一个小裁缝坐在靠窗的台子旁，竭尽全力地做着手中活儿。这时，街上走来一个农家妇女，边走边吆喝：“买果酱啦！物美价廉呀！”\n　　小裁缝觉得这声音挺悦耳，于是就将一头卷发的脑袋伸出了窗外，喊叫道：“上这儿来吧，亲爱的太太，您的货这儿有人要！”\n　　农妇手提沉甸甸的篮子，跨上台阶，来到小裁缝跟前，按照他的吩咐打开一只又一只的罐子。小裁缝挨个仔细察看，还把罐子举到鼻子跟前闻了又闻，最后才说道：“给我来四盎司，亲爱的太太，半镑也行。”\n　　农妇原来以为找到了好买主呢，她把小裁缝要的那一点点果酱如数秤给他之后，就气呼呼地嘟哝着走了。\n　　“愿上帝保佑，”小裁缝嚷嚷道，“这些果酱能给我带来好胃口。”\n　　他从柜子里拿出面包，切了一片下来，把果酱涂在上面。“我心里有数，不会不可口的，”他说，“不过我得先做完这件背心再吃。”\n　　于是，他把涂了果酱的面包放在身旁，继续缝了起来，心里感到美滋滋的，针脚就一针比一针大了。这时，果酱香甜的气味招引来了一群聚在墙上的苍蝇，它们纷纷落在面包上，要品尝一下这美味佳肴。\n　　“哪有你们的份啊？”小裁缝说着把苍蝇赶跑了。苍蝇才不理睬他说了什么，怎么也不肯走，于是落在面包上的苍蝇越来越多了。这下子，小裁缝火冒三丈，随手抓起一条毛巾，朝着苍蝇狠命地打了下去，打死了整整七只苍蝇，有的连腿都给打飞了。\n　　“你可真了不起！”他说道，不禁对自己的勇敢大加赞赏，“全城的人都应该知道你的壮举。”说罢，小裁缝风风火火地为自己裁剪了一条腰带，缝好后，在上面绣了几个醒目的大字：“一下子打死七个！”\n　　“不仅仅是全城，”他突然喊了起来，“还得让全世界的人都知道！”说到这儿，他的心激动得欢蹦乱跳，活像一只小羊羔的尾巴。\n　　小裁缝把腰带系在腰间，打算出去闯世界，因为在他看来，凭着他的英勇无畏精神，再留在小小的作坊里，就大材小用啦。动身前，他四下里搜寻了一番，看看有没有值得带上的东西，却只发现了一快陈干酪，就随手装进口袋里。在门前，他发现灌木丛中绊住了一只小鸟，便捉来放进装干酪的口袋里。\n　　随后，他得意洋洋地上了路。由于个子矮小，他身轻如燕，走起来一点儿也不感到累。走着走着，来到一座大山上。他到了山顶一看，发现一个力大无比的巨人正坐在那儿，悠然自得地环顾左右。小裁缝壮着胆子走到巨人跟前，跟他打招呼：“你好，伙计。你坐在这儿眺望大世界，是吧？我正要去闯闯世界咧，怎么样，有没有心思跟我一快儿去？”\n　　巨人轻蔑地瞟了他一眼，扯着嗓子对他说：“你这个小可怜虫！弱不禁风的小瘪三！”\n　　“啊哈，你这么小看我，是吗？你再往这儿瞧瞧！”小裁缝回答道。说着解开上衣，露出腰带来给巨人看。“你念一念就知道我是何等人啦。”\n　　巨人念了起来：“一下子打死七个”。以为这位裁缝一下子打死的是七个人，心里不禁对小裁缝产生几分敬意。不过，他决心要和小裁缝先试试身手，于是，就拣起一快石头来，用手使劲一捏，捏得石头滴出了水。\n　　“要是你真有力气，”巨人说，“也来这么一手吧。”\n　　“就这个呀？”小裁缝说，“对本人来说，跟玩儿似的。”说着把手伸进口袋里，掏出那快软绵绵的干酪来，轻轻一捏，乳汁就冒了出来。\n　　巨人看了不知说什么才好，却怀疑这么个小人儿是不是真有那么大的力气。随后，他又拣起一快石头来，朝空中猛地一抛，石头飞得那么高，用肉眼几乎看不见了。\n　　“喏，”巨人说，“可怜的小矮子，你也来一下。”\n　　“的确，扔得挺高，”小裁缝回敬道，“可是你扔的那快石头还是掉回到了地上。本人给你露一手，扔出去就不会再掉回来。”\n　　说罢，他从口袋里把那只小鸟抓出来，往空中一扔。重获自由的小鸟欢欢喜喜地飞走了，头也不回地一下便无影无踪。“喂，伙计，这一手还行吧？”小裁缝问道。\n　　“我不否认，扔东西你还行。”巨人回答说，“现在我再瞧瞧你能不能扛动沉重的东西。”\n　　他把小裁缝领到一棵已砍倒在地的大橡树跟前。“你要是真有力气，就帮我把这棵树从林子里抬走。”\n　　“好的，”小裁缝说，“你扛树干，我扛树枝，这树枝可是最难弄的呀。”\n　　巨人扛起树干，小裁缝却坐在了一根树枝上面。巨人没法回头看，不得不整个扛着大树，还扛着坐在树枝上的小裁缝。\n　　小裁缝坐在后面，心旷神怡，快乐地吹着口哨，还唱了几句“三个裁缝骑马出了城”这首歌，抬树对他来说仿佛就是一场游戏而已。\n　　巨人扛着沉重的大树走了一段路程，累得上气不接下气，嚷嚷着说他再也走不动了，必须把树放下来。\n　　小裁缝一下子跳了下来，用两只胳膊抱住树身，做出一副一路上抬着大树的样子，接着对巨人说，“亏你这么个大块头，连棵树也扛不了！”\n　　他们一快儿往前走着，来到一棵樱桃树前，树冠上挂满了熟透的樱桃。巨人一把抓住树冠，拉低后递给小裁缝，让他吃个够。可小裁缝哪有这么大的力气抓住樱桃树呢，巨人一松手，树就忽地一下直起了身，小裁缝也随着被弹到了空中。\n　　小裁缝安然落地，巨人嚷嚷道：“咳！你连抓住这么一根小树枝的力气也没有啊？”\n　　“这和力气有何相干！”小裁缝回答说，“本人一下子能打死七个，你以为我连根小树枝都抓不住吗？林子里有个猎人要朝我开枪，我才急急忙忙跑过树顶。你要是有能耐，跳给我瞧瞧。”\n　　巨人试了一下，却没能跳过去，而被挂在了枝丫间。这样一来，小裁缝又占了上风。\n　　于是，巨人说：“你是一个了不起的小勇士，就请你到我的山洞里去过夜吧。”\n　　小裁缝很愿意，就跟着他去了。他们来到洞中，只见还有一些巨人围坐在火堆旁，个个手里拿着一只烤羊，像吃面包似的在吃着。小裁缝心想：“这儿可比我的作坊好多啦。”巨人指给他一张床，叫他躺下休息。可这张床对小裁缝来说，实在是太大了，他没有躺在床中间，而是爬到了一个角落里。半夜时分，那个巨人以为小裁缝睡熟了，抓起一根大铁钉，照准床上猛地扎了下去，以为把这个小蚱蜢给解决了。\n　　第二天拂晓，巨人们动身到林子里去，把小裁缝忘得一干二净。小裁缝仍然像往常一样活蹦乱跳，无忧无虑，朝他们走去。巨人们一见，以为小裁缝要打死他们，个个吓得屁滚尿流，拔腿就跑。小裁缝呢，继续赶他的路，一直往前走去。\n　　走了很久，小裁缝来到一座王宫的院子里。这时，他已累得精疲力尽，便倒在地上睡着了。他正躺在那儿睡的时候，不少人过来，看见了他腰带上绣的字：“一下子打死七个！”\n　　“哎呀！”他们心想，“这一定是位了不起的英雄。和平时期他到这里来干什么呢？”他们立即去向国王禀报，说一旦战争爆发，此人大有用场，千万不能放他走呵。\n　　国王很赞赏这个主意，便差了一位大臣去找小裁缝，等他一醒来，就请他在军队里效力。这位使者站在一旁，眼睁睁地看着熟睡中的小裁缝，直等到小裁缝伸了伸懒腰，慢慢睁开了双眼，才向他提出请求。\n　　“我正是为此而来的，”小裁缝回答说，“本人很愿意为国王效劳。”\n　　他于是受到了隆重的接待，得到了一处别致的住所。可是其他军官却很妒嫉，巴不得他早点儿远远地离开这里。“要是我们和他打起来，”他们交谈着，“他一下子就能打死我们七个，这可怎么是好呢？我们一败涂地呀。”\n　　后来，他们决定，一快儿去见国王，提出集体辞职。“我们这号人呐，”他们跟国王解释说，“无法和一位一下子就打死七个人的大英雄共事。”\n　　因为一个人而要失去所有忠心耿耿的军官，国王感到十分难过，希望压根儿就没见过这个小裁缝，巴不得能早早把他打发走。可是，国王却没有这个胆量把他赶走，担心小裁缝把他和他的臣民都打死，自己登上王位。他绞尽脑汁，冥思苦想，终于想出一个主意。他派人去告诉小裁缝，说小裁缝是一位出类拔萃、英勇无畏的英雄，因此希望向他做如下提议：\n　　在他的领地上，有一座大森林，林中住着两个巨人，他们俩烧杀抢劫无恶不作，为害极大，可是至今却没有谁敢冒生命危险去和他们较量。要是小裁缝能制服和杀死这两个巨人，国王就答应把自己的独生女儿许配给他，并赐给他半个王国，而且还准备给他派去一百名骑士，为他助阵。\n　　“对你这样一个人来说，这是多么大的鼓舞呀，”小裁缝心里想道，“一位漂亮的公主，还有半个王国，真是千载难逢的好机会啊。”\n　　于是，他回答说：“当然可以啦，我去制服那两个巨人。那一百名骑士嘛，我并不需要他们。我这样一个英雄，一下子能打死七个，那两个怎么会是我的对手呢。”\n　　小裁缝出发了，后面跟着一百名骑士。他们来到森林前，他对这些骑士说：“你们就呆在这儿，我一个人去收拾那两个家伙。”说罢，他独自跑进了林中，一边走着，一边环顾左右。没多大一会儿，就发现了那两个巨人。他们俩躺在一棵大树下正睡觉呢，鼾声如雷，树枝都快被震掉了。\n　　小裁缝忙着把两个口袋装满石头，然后爬到树上。爬到一半时，他悄悄地攀上一根树枝，树枝下边就是那两个熟睡中的巨人的脑袋。接着，他把石头接二连三地朝一个巨人的胸口使劲砸下去。这位大家伙有好一会动也不动一下，后来终于醒了，用力推了推身边的同伴，问道：“你干嘛打我？”\n　　“你在做梦吧，”另一个回答说，“谁打你来着？”\n　　说完，他们俩又躺下睡了。这回，小裁缝把一块石头朝第二个巨人砸了下去。\n　　“干什么？”第二个嚷嚷起来，“干嘛拿石头打我呀？”\n　　“我没有哇。”第一个咆哮着回答说。\n　　他们争吵了几句，却因为感到困乏，又闭上眼睛睡了。小裁缝呢，故伎重演，选了一块最大的石头，朝第一个巨人狠命砸了下去。\n　　“这太不像话啦！”第一个巨人吼了起来。他疯了一样地从地上一跃而起，把他的同伴朝树上猛地一搡，撞得大树都摇晃起来了。第二个分毫不让，以牙还牙，两个家伙怒不可遏，把一棵棵大树连根拔起，朝着对方猛扔过去，最后他们两败俱伤，都倒在地上死了。\n　　小裁缝立即从树上跳了下来。“真是万幸，”他说道，“他们没有拔掉我刚才上的那棵树。”\n　　说罢，他拔出剑来，在每个巨人的胸口上猛刺一剑，然后他走到那些骑士面前说：“完事了，那两个巨人都被我给解决了，可真是一场惊心动魄的遭遇呀。他们见势不妙就把大树连根拔起进行顽抗，当然啦，面对本人这样一下子能打死七个的英雄，那是徒劳的。”\n　　骑士们策马跑进森林一看，两个巨人躺在血泊之中，四周还有连根拔出的大树，这才相信了小裁缝的话。\n　　返回后，小裁缝要求国王把答应给他的奖赏赐给他，国王却后悔了，又左思右想，考虑怎样才能把小裁缝打发走。\n　　“你在得到我的女儿和半个王国之前，”他说，“必须再完成一个壮举。在那座森林里，有一头危害很大的独角兽，你必须把它捕捉住。”\n　　“两个巨人我都没怕，一头独角兽又有什么可怕的呀。”小裁缝吹嘘道。\n　　小裁缝带着一根绳索和一把斧头便动身去了森林，告诉他的随从们在森林外等着。他没找多大功夫，便发现那头独角兽就在眼前，并且正向他直冲过来。\n　　他纹丝不动地站在那里，等独角兽逼近了，敏捷地一下子跳到树后。独角兽发疯似的朝大树撞过来，把角牢牢地戳进了树干里，怎么拔也拔不出来，就被捉住了。\n　　“伙计，这回我可逮住你啦，”小裁缝从树后转出来后说道。他用那根绳索把独角兽的脖子捆了起来，然后用斧头劈开树干，松开兽角，牵着独角兽回去见国王。\n　　谁知国王还是不肯把答应给小裁缝的奖赏赐给他，又提出了第三个条件。他必须再到森林里去把一头危害很大的野猪逮住，然后才举行婚礼。\n　　“我很乐意去，”小裁缝回答说，“逮住一头野猪那还不是跟玩儿似的。”\n　　野猪一见小裁缝，就口里冒着白沫，咬着牙，朝他猛冲过来，想一头把他撞倒在地。谁知勇敢的小裁缝敏捷地跳进了旁边的一座小教堂，眨眼之间，又从窗口跳了出去。野猪追进了教堂，小裁缝从教堂后面几步跑了过来，把门关住，气势汹汹的野猪又重又笨，没法从窗口跳出去，就这样被擒住了。\n　　然后，勇敢的小裁缝去见国王，告诉他说，愿意也罢，不愿意也罢，他这次必须信守诺言，把他的女儿和半个王国赏赐给他。\n　　他们的婚礼隆重举行，欢笑却很少。不过，小裁缝还是当上了国王啦。\n　　不久，年轻的王后在一天夜里听见丈夫说梦话。小裁缝在梦中大声地嚷嚷着：“徒弟，快点儿把这件背心缝好，再把这条裤子补一补，不然我就让你的脑袋尝尝尺子的厉害。”这样一来，她便弄清了她的君主和丈夫是什么出身。\n　　第二天一早就对父亲大发牢骚，抱怨国王给她选择的丈夫只不过是一个下贱的裁缝。\n　　国王安慰她说：“今天晚上，你打开化妆室的门，我派侍从守在外边，等他睡着了，我的侍从就悄悄地进去把他捆起来，然后放到一艘船上，把他送到天涯海角。”\n　　当了国王的小裁缝有个男仆，听见了老国王说的话，就把这个阴谋禀报了主子。\n　　到了晚上，小裁缝像往常一样按时上床就寝，躺在妻子身边。她以为他已经入睡，就从床上爬起来，打开了化妆室的门，然后又躺在床上。小裁缝只是在装睡，这时便开始尖着嗓子喊叫起来：“徒弟，把这件背心缝好，再把这条裤子补一补，不然我就让你的脑袋尝尝尺子的厉害。我一下子打死了七个，杀死了两个巨人，捉住了一头独角兽，还逮住了一头大野猪，难道我还怕化妆室里的哪一个不成？”\n　　听到了小裁缝的这一番话，打算把他捆绑起来的那几个人，个个吓得要死，拔腿就逃走啦。从此，再没有谁敢碰他一根毫毛。就这样，勇敢的小裁缝继续当他的国王，一直当到离开人世。";
    NSString *shiwu = @"　　现在我要讲一个故事！”风儿说。\n　　“不成，请原谅我，”雨儿说，“现在轮到我了！你在街头的一个角落里待得已经够久了，你已经拿出你最大的气力，大号大叫了一通！”\n　　“这就是你对我的感谢吗？”风儿说，“为了你，我把伞吹得翻过来；是的，当人们不愿意跟你打交道的时候，我甚至还把它吹破呢！”\n　　“我要讲话了！”阳光说。“大家请不要作声！”这话说得口气很大，因此风儿就乖乖地躺下来，但是雨儿却摇着风，同时说：“难道我们一定要忍受这吗？这位阳光太太老是插进来。我们不要听她的话！那不值得一听！”\n 　　于是阳光就讲了：“有一只天鹅在波涛汹涌的大海上飞翔。它的每根羽毛像金子一样地发亮。有一根羽毛落到一条大商船上面。这船正挂着满帆在行驶。羽毛落到一个年轻人的卷发上。他管理货物，因此人们把他叫‘货物长’。幸运之鸟的羽毛触到了他的前额，变成了他手中的一杆笔，于是他不久就成了一个富有的商人。他可以买到金马刺，用金盘改装成为贵族的纹章。我在它上面照过。”阳光说。\n　　“这只天鹅在绿色的草原上飞。那儿有一棵孤独的老树；一个七岁的牧羊孩子躺在它下面的荫处休息。天鹅飞过的时候吻了这树上的一片叶子。叶子落到这孩子的手中；这一片叶子变成了三片叶子，然后10片，然后成了一整本书。他在这本书里面读到了自然的奇迹，祖国的语言、信仰和知识。在睡觉的时候，他把这本书枕在他的头下，以免忘记他所读到的东西。这书把他领到学校的凳子和书桌那儿去。我在许多学者之中读到过他的名字！”阳光说。\n　　“天鹅飞到孤寂的树林中去，在那儿沉静、阴暗的湖上停下来。睡莲在这儿生长着，野苹果在这儿生长着，杜鹃和斑鸠在这儿建立起它们的家。\n　　“一个穷苦的女人在捡柴火，在捡落下的树枝。她把这些东西背在背上，把她的孩子抱在怀里，向家里走来。她看到一只金色的天鹅——幸运的天鹅——从长满了灯芯草的岸上飞起来。那儿有什么东西在发着亮呢？有一个金蛋。她把它放在怀里，它仍然是很温暖的；无疑地蛋里面还有生命。是的，蛋壳里发出一个敲击的声音来；她听到了，而且以为这是她自己的心跳。\n　　“在她家里简陋的房间里，她把金蛋取出来。‘嗒！嗒！’它说，好像它是一个很有价值的金表似的，但是它是一个有生命的蛋。这个蛋裂开了，一只小天鹅把它的头伸出来，它的羽毛黄得像真金子。它的颈上有四个环子。因为这个可怜的女人有四个孩子——三个留在家里，第四个她抱着一起到孤寂的森林里去——她马上就懂得了，她的每个孩子将有一个环子。当她一懂得这件事的时候，这只小小的金鸟就飞走了。\n　　“她吻了每一个环子，同时让每一个孩子吻一个环子。她把它放在孩子的心上，戴在孩子的手指上。”\n　　“我看到了！”阳光说，“我看到了随后发生的事情！\n　　“头一个孩子坐在泥坑里，手里握着一把泥。他用指头捏它，它于是就变成了取得金羊毛的雅森①的像。\n　　“第二个孩子跑到草原上去，这儿开着种种不同颜色的花。他摘下一把；他把它们捏得那么紧，甚至把它们里面的浆都挤出来了，射到他的眼睛里去，把那个环子打湿了，刺激着他的思想和手。几年以后，京城的人都把他称为伟大的画家。\n　　“第三个孩子把这个环子牢牢地衔在嘴里，弄出响声——他心的深处的一个回音。思想和感情像音乐似的飞翔，然后又像天鹅似的俯冲到深沉的海里去——思想的深沉的海里去。他成了一个伟大的音乐家。每个国家现在都在想，‘他是属于我的！’\n　　“至于第四个孩子呢，咳，他是一个无人理的人。人们说他是个疯子。因此他应该像病鸡一样，吃些胡椒和黄油！‘吃胡椒和黄油。’他们这么着重地说；他也就吃了。不过我给了他一个阳光的吻。”阳光说。“他一下子得到了我的10个吻。他有诗人的气质，因此他一方面挨了打，一方面又得到了吻。不过他从幸运的金天鹅那里得到了一个幸运的环子。他的思想像一只金蝴蝶似的飞出去了——这是‘不朽’的象征！”\n　　“这个故事太长！”风儿说。\n　　“而且讨厌！”雨儿说，“请在我身上吹几下吧，好使得我的头脑清醒起来。”\n　　于是风儿就吹起来。阳光继续说：“幸运的天鹅在深沉的海湾上飞过去了。渔夫在这儿下了网。他们之中有一个最穷的渔人。他想要结婚，因此他就结婚了。\n　　“天鹅带了一块琥珀给他；琥珀有吸引力，把心都吸到家里去了。琥珀是最可爱的香料。它发出一股香气，好像是从教堂里发出来的；它发出上帝的大自然的香气。他们感到真正的家庭幸福，满足于他们的简朴生活，因此他们的生活成了一个真正的阳光的故事。”\n　　“我们停止好不好？”风儿说。“阳光已经讲得够长了。我听厌了！”\n　　“我也听厌了！”雨儿说。\n　　“我们听到这些故事的人怎么说呢？”\n　　我们说：“现在它们讲完了！”\n　　－－－－－－－－－－－－－－－－－－&nbsp;\n  　他父亲的王国被他的异母兄弟贝立亚斯（Pelian）占领。他长大了去索取这个王国；贝立亚斯说，如果雅森能把被一条恶龙看守着的金羊的毛取来，他就可以交还王国。雅森终于把恶龙降服，取来了金羊毛。";
    
    NSString *shiliu = @"　　在一株老树的裂缝里有好几只蜥蜴在活泼地跑着。它们彼此都很了解，因为它们讲着同样的蜥蜴语。\n　　“嗨，住在老妖精山上的那些家伙号叫得才厉害呢！”一只蜥蜴说，“他们的闹声把我弄得两整夜合不上眼睛。这简直跟躺在床上害牙痛差不多，因为我横竖是睡不着的！”\n　　“那儿一定有什么事情！”另一只蜥蜴说。“他们把那座山用四根红柱子支起来，一直支到鸡叫为止。这座山算是痛痛快快地通了一次风；那些女妖还学会了像跺脚这类的新舞步呢。那儿一定有什么事情！”\n　　“对，我刚才还跟我认识的一位蚯蚓谈起过这件事，”第二只蜥蜴说。“这位蚯蚓是直接从山里来的——他昼夜都在那山里翻土。他听到了许多事情。可怜的东西，他的眼睛看不见东西，可是他却知道怎样摸路和听别人谈话。妖山上的人正在等待一些客人到来——一些有名望的客人。不过这些客人究竟是谁，蚯蚓可不愿意说出来——也许他真的不知道。所有的鬼火都得到了通知，要举行一个他们所谓的火炬游行。他们已经把金银器皿——这些东西他们山里有的是——擦得焕然一新，并且在月光下摆出来啦！”\n　　“那些客人可能是谁呢？”所有的蜥蜴一齐问。“那儿在发生什么事情呢！听呀，多么闹！多么吵！”\n　　正在这时候，妖山开了。一位老妖小姐①急急忙忙地走出来。她的衣服穿得倒蛮整齐，可就是没有背。她是老妖王的管家娘娘，也是他的一个远房亲戚。她的额角上戴着一颗心形的琥珀。她的一双腿动得真够快：得！得！嗨，她才会走呢！她一口气走到住在沼泽地上的夜乌鸦那儿去。\n　　“请你到妖山上去，今晚就去，”她说。“不过先请你帮帮忙，把这些请帖送出去好吗？您自己既然无家可管，你总得做点事情呀！我们今天有几个非常了不起的客人——很重要的魔法师。老国王也希望借这个机会排场一下！”\n　　“究竟要请一些什么客人呢？”夜乌鸦问。\n　　“嗳，谁都可以来参加这个盛大的跳舞会，甚至人都可以来——只要他们能在睡梦中讲话，或者能懂一点我们所做的事情。不过参加第一次宴会的人可要挑选一下；我们只能请最有名的人。我曾经跟妖王争论过一次，因为我坚持我们连鬼怪也不能请。我们得先请海人和他的一些女儿。他们一定很喜欢来拜访干燥的陆地的。不过他们得有一块潮湿的石头，或者比这更好的东西，当做座位；我想这样他们就不好意思拒绝不来了。我们也可以请那些长有尾巴的头等魔鬼、河人和小妖精来。我想我们也不应该忘记墓猪、整马和教堂的小鬼②。事实上他们都是教会的一部分，跟我们这些人没有关系。但是那也不过是他们的职务，他们跟我们的来往很密切，常常拜访我们！”\n　　“好极了！”夜乌鸦说，接着他就拿着请帖飞走了。\n　　女妖们已经在妖山上跳起舞来了。她们披着雾气和月光织成的长围脖跳。凡是喜欢披这种东西的人，跳起来倒是蛮好看的。妖山的正中央是一个装饰得整整齐齐的大客厅。它的地板用月光洗过一次，它的墙用巫婆的蜡油擦过一番，因此它们就好像摆在灯面前的郁金香花瓣似的，射出光辉。厨房里全是烤青蛙、蛇皮色的小孩子的手指、毒菌丝拌的凉菜、湿耗子鼻、毒胡萝卜等；还要沼泽地里巫婆熬的麦酒③和从坟窖里取来的亮晶晶的硝石酒。所有的菜都非常实在，甜菜中包括生了锈的指甲和教堂窗玻璃碎片这几个菜。\n　　老妖王用石笔把他的金王冠擦亮。这是一根小学六年级用的石笔，而老妖王得到一根六年级用的石笔是很不容易的！他的睡房里挂着幔帐，而这幔帐是用蜗牛的分泌物粘在一起的。是的，那里面传出一阵吱吱喳喳的声音。摘自七故事网 &nbsp;www.qigushi.com\n　　“现在我们要焚一点马尾和猪鬃，当做香烧；这样，我想我的工作可算是做完了！”老妖小姐说。\n　　“亲爱的爸爸！”最小的女儿说，“我现在可不可以知道，我们最名贵的客人是些什么人呢？”\n　　“嗯，”他说，“我想我现在不得不公开宣布了！我有两个女儿应该准备结婚！她们两个人必须结婚。挪威的那位老地精将要带着他的两个少爷到来——他们每人要找一个妻子。这位老地精住在老杜伏尔山中，他有好几座用花岗石筑的宫堡，还有一个谁都想象不到的好金矿。这位老地精是一个地道的、正直的挪威人，他老是那么直爽和高兴。在我跟他碰杯结为兄弟以前，我老早就认识他。他讨太太的时候到这儿来过。现在她已经死了。她是莫恩岩石王的女儿。真是像俗话所说的，他在白垩岩上讨太太④。啊，我多么想看看这位挪威的地精啊！他的孩子据说是相当粗野的年轻人，不过这句话可能说得不公平。他们到年纪大一点就会变好的。我倒要看看，你们怎样把他们教得懂事一点。”\n　　“他们什么时候到来呢？”一个女儿问。\n　　“这要看风色和天气而定，”老妖王说，“他们总是找经济的办法旅行的！他们总是等机会坐船来。我倒希望他们经过瑞典来，不过那个老家伙不是这么想法！他赶不上时代——这点我不赞成！”\n　　这时有两颗鬼火跳过来了。这一个跳得比另一个快，因此快的那一个就先到。\n　　“他们来了！他们来了！”他们大声叫着。\n　　“快把我的王冠拿来，我要站进月光里去！”老妖王说。\n　　几个女儿把她们的长围脖拉开，把腰一直弯到地上。\n　　杜伏尔的老地精就站在他们面前。他的头上戴着坚硬的冰柱和光滑的松球做成的王冠；此外，他还穿着熊皮大衣和滑雪的靴子。他的儿子恰恰相反，脖子上什么也没有围，裤子上也没有吊带，因为他们都是很结实的人。\n　　“这就是那个土堆吗？”最年轻的孩子指着妖山问。“我们在挪威把这种东西叫做土坑。”\n　　“孩子！”老头子说，“土坑向下洼，土堆向上凸，你的脑袋上没有长眼睛吗？”\n　　他们说他们在这儿惟一感到惊奇的事情是，他们懂得这儿的语言。\n　　“不要在这儿闹笑话吧！”老头儿说，“否则别人以为你们是乡巴佬！”\n　　他们走进妖山。这儿的客人的确都是上流人物，而且在这样短促的时间内就都请来了。人们很可能相信他们是风吹到一起的。每个客人的座位都是安排得既舒服而又得体。海人的席位是安排在一个水盆里，因此他们说，他们简直像在家里一样舒服。每人都很有礼貌，只是那两个小地精例外。他们把腿跷到桌子上，但是他们却以为这很适合他们的身份！\n　　“把脚从盘子上拿开！”老地精说。他们接受了这个忠告，可并不是马上就改。他们用松球在小姐们身上呵痒；他们为了自己的舒服，把靴子脱下来叫小姐们拿着。不过他们的爸爸——那个老地精——跟他们完全两样。他以生动的神情描述着挪威的那些石山是怎样庄严，那些溅着白泡沫的瀑布怎样发出雷轰或风琴般的声音。他叙述鲑鱼一听到水精弹起金竖琴时就怎样逆流而上。他谈起在明朗的冬夜里，雪橇的铃是怎样叮当叮当地响，孩子们怎样举着火把在光滑的冰上跑，怎样把冰照得透亮，使冰底下的鱼儿在他们的脚下吓得乱窜。的确，他讲得有声有色，在座的人简直好像亲眼见过和亲耳听过似的：好像看见锯木厂在怎样锯木料，男子和女子在怎样唱歌和跳挪威的“哈铃舞”。哗啦！这个老地精出乎意料地在老妖小姐的脸上接了一个响亮的“舅舅吻”⑤。这才算得是一个吻呢！不过他们并不是亲戚。\n　　现在妖小姐们要跳舞了。她们跳普通步子，也跳蹬脚的步子。这两种步子对她们都很适合。接着她们就跳一种很艺术的舞——她们也把它叫做“前无古人、后无来者”的舞。乖乖！她们的腿动得才灵活呢！你简直分不出来，哪里是开头，哪里是结尾；你也看不清楚，哪里是手臂，哪里是腿。它们简直像刨花一样，搅混得乱七八糟。她们跳得团团转，把“整马”弄得头昏脑涨，不得不退下桌子。\n　　“嘘嘘！”老地精说，“这才算得是一回大腿的迷人舞呢！不过，她们除了跳舞、伸伸腿和扇起一阵旋风以外，还能做什么呢？”\n　　“你等着瞧吧！”妖王说。\n　　于是他把最小的女儿喊出来。她轻盈和干净得像月光一样；她是所有姊妹之中最娇嫩的一位。她把一根白色的木栓放在嘴里，马上她就不见了——这就是她的魔法。\n　　不过老地精说，他倒不希望自己的太太有这样一套本领。他也不认为他的儿子喜欢这套本领。\n　　第二个女儿可以跟自己并排走，好像她有一个影子似的——但是山精是没有影子的。\n　　第三个女儿有一套完全不同的本领。她在沼泽女人的酒房里学习过，所以她知道怎样用萤火虫在接骨木树桩上擦出油来。\n　　“她可以成为一个很好的家庭主妇！”老地精说。他对她挤了挤眼睛代替敬酒，因为他不愿意喝酒太多。\n　　现在第四个妖姑娘来了。她有一架很大的金竖琴。她弹第一下的时候，所有的人就都得照她的意思动作。\n　　“这是一个危险的女人！”老地精说。不过他的两位少爷都已从山里走出来，因为她们已经感到腻了。\n　　“下一位小姐能够做什么呢？”老地精问。\n　　“我已经学会了怎样爱挪威人！”她说，“如果我不能到挪威去，我就永远不结婚！”\n　　不过最小的那个女儿低声对老地精说：“这是因为她曾经听过一支挪威歌的缘故。歌里说，当世界灭亡的时候，挪威的石崖将会仍然作为纪念碑而存在。所以她希望到挪威去，因为她害怕灭亡。”\n　　“呵！呵！”老地精说，“这倒是说的心坎里的话！最后的第七个小姐能够做什么呢？”\n　　“第七位头上还有第六位呀！”妖王说，因为她不会计算数字。可是那第六位小姐却姗姗地不愿意出来。\n　　“我只能对人讲真话！”她说，“谁也不理我，而我做我的寿衣已经够忙的了！”\n　　这时第七位，也是最后的一位，走出来了。她能够做什么呢？她能讲故事——要她讲多少就能讲多少。\n　　“这是我的五个指头？”老地精说。“把每个指头编一个故事吧！”\n　　这位姑娘托起他的手腕，她笑得连气都喘不过来。它戴着一个戒指，好像它知道有人快要订婚似的，当她讲到“金火”的时候，老地精说，“把你握着的东西捏紧吧，这只手就是你的！我要讨你做太太！”\n　　妖姑娘说，“‘金火’和‘比尔——玩朋友’⑥的故事还没有讲完！”\n　　“留到冬天再讲给我听吧！”老地精说。“那时我们还可以听听关于松树的故事，赤杨的故事，山妖送礼的故事和寒霜的故事！你可以尽量讲故事，因为那儿还没有人会这一套！那时我们可以坐在石室里，烧起松木来烤火，用古代挪威国王的角形金杯盛蜜酒喝——山精送了两个这样的酒杯给我！我们坐在一起，加尔波⑦将会来拜访我们，他将对你唱着关于山中牧女的歌。那才快乐呢。鲑鱼在瀑布里跳跃，撞着石壁，但是却钻不进去！嗨，住在亲爱的老挪威才痛快呢！但是那两个孩子到什么地方去了？”\n　　是的，那两个孩子到什么地方去了呢？他们在田野里奔跑，把那些好心好意准备来参加火炬游行的鬼火都吹走了。\n　　“你们居然这样胡闹！”老地精说，“我为你们找到了一个母亲。现在你们也可以在这些姨妈中挑一个呀！”\n　　不过少爷说，他们喜欢发表演说，为友情干杯，但是没有心情讨太太。因此他们就发表演说，为友情干杯，而且还把杯子套在手指尖上，表示他们真正喝干了。他们脱下上衣倒在桌子上呼呼地睡起来，因为他们不愿意讲什么客套。但是老地精跟他的年轻夫人在房里跳得团团转，而且还交换靴子，因为交换靴子比交换戒指好。\n　　“现在鸡叫了！”管家的老妖姑娘说。“我们现在要把窗扉关上，免得太阳烤着我们！”\n　　这样，妖山就关上了。\n　　不过外面的那四只蜥蜴在树的裂口里跑上跑下。这个对那个说：“啊，我喜欢那个挪威的老地精！”\n　　“我更喜欢他的几个孩子！”蚯蚓说。不过，可怜的东西，他什么也看不见。\n　　－－－－－－－－－－－－－－－－－－－－－－－－－－－－－－－－－－\n　　①原文是Elverpige，据丹麦的传说，老妖小姐像一个假面具，前面很好看，后面则是空的。\n　　②根据丹麦的古老迷信，每次建造一个教堂的时候，地下就要活埋一匹马。凡是一个人要死，这匹马就用三只腿在夜里走到他家里来。有些教堂活埋一只猪。这只猪的魂魄叫做“墓猪”。“教堂小鬼”（Kirkegrimen）专门看守墓地；他惩罚侵害墓地的人。\n　　③根据丹麦的传说，沼泽地上住着一个巫婆。她天天在熬麦酒。天下雾就是她熬酒时冒出来的水蒸气。\n　　④这是丹麦的一个成语：“白垩岩上讨太太”（Han tog sin kone paa krjd），即“不费一文讨太太”的意思。\n　　⑤原文是Morbroder-Smadsk，意义不明。许多其他文字的译者干脆把它译成“一个吻”。大概这种吻是亲戚之间的一种表示亲热的吻，没有任何其他的意义。\n　　⑥这儿是双关的意思，根据欧洲的习惯，把手交给谁，即答应跟谁订婚的意思。\n　　⑦这是挪威传说中的一种善良的田野妖精。";
    self.gushi_arr = @[one,two,three,four,five,six,seven,eight,nine,ten,shiyi,shier,shisan,shisi,shiwu,shiliu];
}



